tasks.command:
echo "/* Controls Modal */
#controls-modal {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: rgba(0, 0, 0, 0.95);
    padding: 40px;
    border: 2px solid var(--neon-pink);
    box-shadow: 0 0 30px var(--neon-pink);
    text-align: center;
    z-index: 100;
    min-width: 600px;
}

#controls-modal h2 {
    color: var(--neon-pink);
    margin-bottom: 30px;
    text-shadow: 0 0 10px var(--neon-pink);
}

.controls-grid {
    display: flex;
    justify-content: space-around;
    margin-bottom: 30px;
    text-align: left;
}

.control-col h3 {
    color: var(--neon-blue);
    border-bottom: 1px solid var(--neon-blue);
    padding-bottom: 10px;
}

.control-col p {
    margin: 10px 0;
    font-size: 1.1rem;
}

#btn-close-controls {
    border-color: var(--neon-pink);
    color: var(--neon-pink);
}

#btn-close-controls:hover {
    background: var(--neon-pink);
    color: black;
    box-shadow: 0 0 15px var(--neon-pink);
}" >> style.css
